mapper.select:
select * from inlist


       and ILBy=#{ILBy}

    and ILDate >= #{startTime}
    and ILDate < #{endTime}

GROUP BY ILID

select sum(ILNum) as ILNum,SUM(ILTotal) as ILTotal

    ,sID


    ,goodName

from inlist

    and ILStatus='1'
    and orderOinion='1'

        and sID=#{sID}


        and goodName=#{goodName}

    and ILDate >= #{startTime}
    and ILDate < #{endTime}
